	public static List<String> socketParase(String text) {
		if (text == null || "".equals(text)) {
			return null;
		}
		String checkFlag =  MyUtils.regText(text,SysParamConst.PROTOCOL_NET) ;
		if(checkFlag == null){
			return null ;
		}
		List<String> results = new ArrayList<String>();
		String[] messages = text.split("\\s");
		if ("88".equals(messages[0])) {
			results.add("传输头,88");
		}
		if ("10".equals(messages[1])) {
			results.add("优先权,4");
		}
		if ("18".equals(messages[1])) {
			results.add("优先权,6");
		}
		if (ProtocolConst.addrMap.containsKey(messages[3])) {
			results.add("目的地址," + ProtocolConst.addrMap.get(messages[3]));
		}
		if (ProtocolConst.addrMap.containsKey(messages[4])) {
			results.add("源地址," + ProtocolConst.addrMap.get(messages[4]));
		}
		if (commands.get(messages[2] + "00") != null) {
			Command cm = (Command) commands.get(messages[2] + "00");
			results.add("报文帧," + cm.getName());
			for (int i = 0; i < cm.getDatas().size(); i++) {
				Data obj = cm.getDatas().get(i);
				switch (obj.getFormat()) {
				case "BIN":
					results = binFormat(results, obj, messages, 0);
					break;
				case "ASCII":
					results = asciFormat(results, obj, messages);
					break;
				case "BCD":
					results = bcdFormat(results, obj, messages);
					break;
				case "CP56time2a":
					results = cpTimeFormat(results, obj, messages);
					break;	
				case "L-BIT":
					results = lbitFormat(results, obj, messages);
					break;
				case "T-BIN":
					results = binFormat(results, obj, messages, 1);
					break;
				default:
					return null;
				}

			}
		} else if (commands.get(messages[2] + messages[4].substring(0, 1) + "0") != null) {
			Command cm = (Command) commands.get(messages[2] + messages[4].substring(0, 1) + "0");
			results.add("报文帧," + cm.getName());
			for (int i = 0; i < cm.getDatas().size(); i++) {
				Data obj = cm.getDatas().get(i);
				switch (obj.getFormat()) {
				case "BIT":
					results = bitFormat(results, obj, messages);
					break;
				default:
					return null;
				}
			}
			
		} else {
			if ("30".equals(messages[2]) || "31".equals(messages[2])) {
				String dt = messages[4].substring(0, 1);
				if (commands.get(dt) != null) {
					if (commands.get(dt) instanceof DTCommand) {
						DTCommand dtcm = (DTCommand) commands.get(dt);
						List processData = new ArrayList();
						for (int i = 0; i < dtcm.getDtDatas().size(); i++) {
							DTData dtObj = dtcm.getDtDatas().get(i);
							if (dtObj.getIndex().equals(messages[5])) {
								processData.add(dtObj);
							}
						}
						for (int j = 0; j < processData.size(); j++) {
							DTData dtObj = (DTData) processData.get(j);
							if (dtObj.getIndex().equals("01")) {
								results.add("name=长度,value=" + messages[6]);
							}
							switch (dtObj.getFormat()) {
							case "BIN":
								results = binFormat_yc(results, dtObj, messages);
								break;
							case "ASCII":
								results = asciFormat_yc(results, dtObj, messages);
								break;	
							default:
								return null;
							}
						}
					}
				}
			}
		}

		return results;
	}

	public static List<String> binFormat(List<String> list, Data obj, String[] messages, int trance) {
		int[] postions = obj.getPostions();
		int len = obj.getLen();
		String value = "";
		if (len == 1) {
			if (trance == 0) {
				value = messages[4 + postions[0]];
			} else {
				value = MyUtils.hexToTen(messages[4 + postions[0]]);
			}
		} else {
			String hexStr = "";
			for (int i = postions.length - 1; i > 0; i--) {
				hexStr += messages[postions[i] + 4];
			}
			value = MyUtils.hexToTen(hexStr);
		}
		if (obj.getOptionflag()) {
			Map<String, String> options = obj.getOptions();
			list.add("name=" + obj.getName() + ",value=" + options.get(value));
		} else {
			list.add("name=" + obj.getName() + ",value=" + value);
		}
		return list;
	}

	public static List<String> asciFormat(List<String> list, Data obj, String[] messages) {
		int[] postions = obj.getPostions();
		String value = "";
		String hexStr = "";
		for (int temp : postions) {
			hexStr += messages[temp + 4];
		}
		value = MyUtils.hex2Str(hexStr);
		list.add("name=" + obj.getName() + ",value=" + value);
		return list;
	}

	public static List<String> bcdFormat(List<String> list, Data obj, String[] messages) {
		int[] postions = obj.getPostions();
		String hexStr = "";
		for (int temp : postions) {
			hexStr += messages[temp + 4];
		}
		list.add("name=" + obj.getName() + ",value=" + hexStr);
		return list;
	}
	public static List<String> cpTimeFormat(List<String> list, Data obj, String[] messages) {
		int[] postions = obj.getPostions();
		String[] params = new String[obj.getLen()] ;
		for (int temp : postions) {
			params[temp-1]= messages[temp + 4];
		}
		list.add("name=" + obj.getName() + ",value=" + MyUtils.toCP56Time2aFormat(params));
		return list;
	}
	
	
	public static List<String> lbitFormat(List<String> list, Data obj, String[] messages) {
		int[] postions = obj.getPostions();
		String hexStr = messages[postions[0] + 4];
		String bits = MyUtils.hexStringToByte(hexStr);
		Map<String, String> options = obj.getOptions();
		list.add("name=" + obj.getName() + ",value=" + options.get(bits.charAt(bits.length() - 1) + ""));
		return list;
	}
	

	public static List<String> bitFormat(List<String> list, Data obj, String[] messages) {
		int[] postions = obj.getPostions();
		int len = obj.getLen();
		int group = Integer.parseInt(obj.getGroup()) ;
		String value =  messages[4 + group ] ;
		if(group ==6){
			System.out.println();
		}
		String bValue = MyUtils.hexStringToByte(value) ;
		if(len == 1){
			bValue = bValue.charAt(8-postions[0])+"";
		}else{
			String rValue = "" ;
			for(int a = 0 ; a< postions.length ;a++){
				rValue =bValue.charAt(8-postions[a])+rValue ;
			}
			bValue = MyUtils.binaryToTen(rValue)+"";
		}
		if (obj.getOptionflag()) {
			Map<String, String> options = obj.getOptions();
			list.add("name=" + obj.getName() + ",value=" + options.get(bValue));
		}else{
			list.add("name=" + obj.getName() + ",value=" + bValue);
		}
		return list;
	}
	public static List<String> binFormat_yc(List<String> list, DTData dtObj, String[] messages) {

		int len = dtObj.getLen();
		String value = "";
		if (len == 1) {
			value = messages[4 + dtObj.getStart()];
		} else {

			value = MyUtils.hexToTen(messages[4 + dtObj.getStart()] + messages[5 + dtObj.getStart()]);
		}
		if (dtObj.getOptions() != null) {
			Map<String, String> options = dtObj.getOptions();
			list.add("name=" + dtObj.getName() + ",value=" + options.get(value));
		} else {
			list.add("name=" + dtObj.getName() + ",value=" + value);
		}
		return list;
	}

	public static List<String> asciFormat_yc(List<String> list, DTData dtObj, String[] messages) {
		int len = dtObj.getLen();
		String value = "";
		String hexStr = "";
		for (int i = 0; i < len; i++) {
			hexStr += messages[6 + i];
		}
		value = MyUtils.hex2Str(hexStr);
		list.add("name=" + dtObj.getName() + ",value=" + value);
		return list;
	}
